(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),m=()=>{let t=(()=>{let t=(new Date("28 april 2022").getTime()-(new Date).getTime())/1e3,e=Math.floor(t/60/60),n=Math.floor(t/60%60),o=Math.floor(t%60);return e=e<10?"0"+e:e,n=n<10?"0"+n:n,o=o<10?"0"+o:o,{timeRemaining:t,hours:e,minutes:n,seconds:o}})();e.textContent=t.hours,n.textContent=t.minutes,o.textContent=t.seconds;let i=setInterval;t.timeRemaining>0&&setInterval(m,1e3),t.timeRemaining<=0&&(clearInterval(i),e.textContent="00",n.textContent="00",o.textContent="00")};m()})()})();